import "./App.scss";
import React, { useState, Suspense } from "react";
import { Canvas } from "react-three-fiber";
import BlueSpheres from "./Components/BlueSpheres";
import WhiteSpheres from "./Components/WhiteSpheres";
import Environment from "./Components/Environment";
import Lights from "./Components/Lights";
import Animations from "./Components/Animations/Animations";
import { Button, Container, Row, Col, Navbar, Nav } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import MiniAxe from "./Assets/images/DestinyAxeSmall.png";
import { SocialIcon } from "react-social-icons";
import GoogleFontLoader from "react-google-font-loader";

import {
  BrowserRouter as Router,
  Route,
  Switch,
  Link,
  Redirect,
} from "react-router-dom";
import BottomForeground from "./Components/BottomForeground";

const App = (props) => {
  const [showForeground, setShowForeground] = useState(false);

  const [isDisplayed, setIsDisplayed] = useState(true);

  const [redirectTo, setRedirectTo] = useState("/");
  const toggleShowHide = () => {
    setIsDisplayed(false);
    setShowForeground(true);
    setRedirectTo("/home");
  };
  const myCallback = (dataFromChild) => {
    setShowForeground(dataFromChild);
    setRedirectTo("/home");
  };

  return (
    <Router>
      <div className="App">
        {/* A <Switch> looks through its children <Route>s and
            renders the first one that matches the current URL. */}

        <Switch>
          <Route path={["/", "/home"]}>
            <Redirect to={redirectTo}></Redirect>
            {/*<div>*/}
            {/*  <Button onClick={toggleShowHide} />*/}
            {/*</div>*/}

            <Navbar expand="lg" className="  navGradient">
              <Navbar.Brand href="home">
                <img
                  src={MiniAxe}
                  className=" pt-2 pb-2 d-inline-block align-top"
                  alt="React Bootstrap logo"
                />
              </Navbar.Brand>
              <Navbar.Toggle aria-controls="basic-navbar-nav" />
              <Nav className="mr-auto h2 font-weight-bold">
                <Nav.Link className="text-warning" href="home">
                  Shawn Mountenay
                </Nav.Link>
              </Nav>
              <Nav className="ml-auto ">
                <SocialIcon
                  className="mr-2"
                  url="https://www.linkedin.com/in/shawn-m-045995150"
                  fgColor="white"
                  bgColor="black"
                />

                <SocialIcon
                  network="email"
                  url="mailto:shawn.mountenay@gmail.com"
                  bgColor="black"
                  fgColor="white"
                />
              </Nav>
            </Navbar>

            {/* <Button
              className="fixed-top mt-2 ml-5"
              variant="primary"
              onClick={toggleShowHide}
            >
              TEST
            </Button> */}

            <Canvas camera={{ position: [0, 0, 4] }}>
              <WhiteSpheres />
              <BlueSpheres />
              <Lights />
              <Environment />

              {isDisplayed ? (
                <Suspense fallback={null}>
                  <Animations
                    callbackFromParent={myCallback}
                    isDisplayed={isDisplayed}
                    showForeground={showForeground}
                  />
                </Suspense>
              ) : null}
            </Canvas>

            {showForeground ? null : <BottomForeground />}

            {showForeground ? (
              <Button variant="primary" onClick={toggleShowHide} />
            ) : null}
            {/* <Button onClick={toggleShowHide} /> */}
          </Route>
        </Switch>
      </div>
    </Router>
  );
};
export default App;

function Home() {
  return (<h2>Home</h2>), (<p>biatch</p>);
}

function Users() {
  return <h2>Users</h2>;
}

const ConsoleLog = ({ children, redirectTo }) => {
  console.log("foreGround is " + children);
  return null;
};
