import "./App.css";
import React, { useState, Suspense } from "react";
import { Canvas } from "react-three-fiber";
import BlueSpheres from "./Components/BlueSpheres";
import WhiteSpheres from "./Components/WhiteSpheres";
import Environment from "./Components/Environment";
import Lights from "./Components/Lights";
import Animations from "./Components/Animations/Animations";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Link,
  Redirect,
} from "react-router-dom";
import BottomForeground from "./Components/BottomForeground";

const App = (props) => {
  const [showForeground, setShowForeground] = useState(false);

  const [isDisplayed, setIsDisplayed] = useState(true);

  const [redirectTo, setRedirectTo] = useState("/");
  const toggleShowHide = () => {
    setIsDisplayed(false);
    setShowForeground(true);
    setRedirectTo("/home");
  };
  const myCallback = (dataFromChild) => {
    setShowForeground(dataFromChild);
    setRedirectTo("/home");
  };

  return (
    <Router>
      <div className="App">
        {/* A <Switch> looks through its children <Route>s and
            renders the first one that matches the current URL. */}

        <Switch>
          <Route path={["/", "/home"]}>
            <Redirect to={redirectTo}></Redirect>
            {/*<div>*/}
            {/*  <Button onClick={toggleShowHide} />*/}
            {/*</div>*/}
            <Canvas camera={{ position: [0, 0, 4] }}>
              {/* <oControls/> */}
              <WhiteSpheres />
              <BlueSpheres />
              <Lights />
              <Environment />

              {isDisplayed ? (
                <Suspense fallback={null}>
                  {/* <PullRelease /> */}
                  <Animations
                    callbackFromParent={myCallback}
                    isDisplayed={isDisplayed}
                    showForeground={showForeground}
                  />
                </Suspense>
              ) : null}

              {/* <Dodecahedron /> */}
            </Canvas>

            {showForeground ? <BottomForeground /> : null}

            {showForeground ? <button onClick={toggleShowHide} /> : null}
            {/* <Button onClick={toggleShowHide} /> */}
          </Route>
        </Switch>
      </div>
    </Router>
  );
};
export default App;

function Home() {
  return (<h2>Home</h2>), (<p>biatch</p>);
}

function Users() {
  return <h2>Users</h2>;
}

const ConsoleLog = ({ children, redirectTo }) => {
  console.log("foreGround is " + children);
  return null;
};
const Button = ({ onClick }) => (
  <div class="button">
    <style jsx="">
      {`
        .button {
          position: fixed;
          bottom: 250px;
          left: 100px;
        }
      `}
    </style>

    <button onClick={onClick} type="button">
      {/* <Link to="/home">Toggle Show/Hide</Link> */}
      Show
    </button>
  </div>
);
